const sign_in_button = document.querySelector("#sign-in-btn");
const sign_up_button = document.querySelector("#sign-up-btn");
const box_container = document.querySelector(".box-container");

sign_up_button.addEventListener("click", () => {
    box_container.classList.add("sign-up-mode");
})

sign_in_button.addEventListener("click", () => {
    box_container.classList.remove("sign-up-mode");
})
